mapper.update:
	UPDATE BOARD_STATICS
   SET LATEST_VIEWED_DATE = SYSDATE
 WHERE BID = #{bId}

	UPDATE BOARD
   SET VIEWCNT = #{viewCnt} + 1
 WHERE BID = #{bId}

UPDATE BOARD
   SET BTITLE = #{bTitle}
   , BCONTENT = #{bContent}
   , CATEGORYID = #{cateId}
   , DEALMETHOD = #{dealMethod}
   , PCONDITION = #{pCondition}
   , PRICE =#{price}
 WHERE BID = #{bId}
